public class Deadline extends Task {    private String deadline;    public Deadline(String taskName, String taskDeadline)    {        super(taskName);        deadline = taskDeadline;    }    @Override    public String toString()    {        return "[D]" + super.doneTitle() + super.getName() + "(by: " + deadline + ")";    }    public String toFile() {        if (super.getDone()) {            return "D D " + super.getName() + "/by" + deadline;        } else {            return "D N " + super.getName() + "/by" + deadline;        }    }}