public class Event extends Task {    private String fromDate;    private String toDate;    public Event(String taskName, String taskFromDate, String taskToDate)    {        super(taskName);        fromDate = taskFromDate;        toDate = taskToDate;    }    @Override    public String toString()    {        return "[E]" + super.doneTitle() + super.getName() + "(from: " + fromDate + "to: " + toDate + ")";    }}