package Yapper;public class Parser {    private final TaskList taskList;    public Parser(TaskList taskList) {        this.taskList = taskList;    }    public void readCommand(String command) {        try {            if (command.equals("bye")) {                this.taskList.exit();                System.exit(0);            } else if (command.equals("list")) {                this.taskList.returnList();            } else if (command.startsWith("mark")) {                this.taskList.mark(command);            } else if (command.startsWith("unmark")) {                this.taskList.unmark(command);            } else if (command.startsWith("todo")) {                this.taskList.addToDo(command);            } else if (command.startsWith("deadline")) {                this.taskList.addDeadline(command);            } else if (command.startsWith("event")) {                this.taskList.addEvent(command);            } else {                if (!command.startsWith("delete")) {                    throw new YapperException("Yapper don't know this command :(");                }                this.taskList.deleteTask(command);            }        } catch (YapperException e) {            System.out.println(e.getMessage());        }    }}