package Yapper;public abstract class Task {    public static final String DONE_SYMBOL = "[X]";    public static final String UNDONE_SYMBOL = "[ ]";    public static final String FILE_DONE_SYMBOL = "D";    public static final String FILE_UNDONE_SYMBOL = "N";    private String name;    private boolean done;    /**     * Creates an instance of a Task.     *     * @param taskName Name of the task.     */    public Task(String taskName) {        this.name = taskName;    }    public void setDone(boolean bool) {        this.done = bool;    }    public boolean getDone() {        return this.done;    }    public String getName() {        return this.name;    }    /**     * Returns the string representation of the Task object to be recorded into a file.     * String to be decoded when loading history from the file.     *     * @return String representation of the Task object.     */    public abstract String toFile();    /**     * Returns True if task name contains the string word, else False.     *     * @param word Word to search in task name.     * @return Boolean True if task name contains the string word, else False.     */    public boolean contains(String word) {        return name.contains(word);    }}