package Yapper;public abstract class Task {    public static final String DONE_SYMBOL = "[X]";    public static final String UNDONE_SYMBOL = "[ ]";    public static final String FILE_DONE_SYMBOL = "D";    public static final String FILE_UNDONE_SYMBOL = "N";    private String name;    private boolean done;    public Task(String taskName) {        this.name = taskName;    }    public void setDone(boolean bool) {        this.done = bool;    }    public boolean getDone() {        return this.done;    }    public String getName() {        return this.name;    }    public abstract String toFile();}