package Yapper;import java.util.Scanner;public class Ui {    private final Scanner sc;    private final Parser parser;    /**     * Creates an instance of UI     *     * @param parser Parser object for UI to send user inputs to be parsed.     */    public Ui(Parser parser) {        this.sc = new Scanner(System.in);        this.parser = parser;    }    /**     * Reads user inputs and sends it to the parser to parse.     */    public void readInput() {        while(true) {            if (sc.hasNextLine()) {                String text = sc.nextLine();                this.parser.readCommand(text);            }        }    }}