package Yapper;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.assertEquals;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.IOException;import java.io.PrintStream;public class ParserTest {    @Test    public void invalidInput() throws IOException {        String userInput = String.format("blah%bye", System.lineSeparator());        ByteArrayInputStream bais = new ByteArrayInputStream(userInput.getBytes());        System.setIn(bais);        String expected = "Yapper don't know this command :(";        ByteArrayOutputStream baos = new ByteArrayOutputStream();        PrintStream printStream = new PrintStream(baos);        System.setOut(printStream);        Yapper.main(null);        String[] lines = baos.toString().split(System.lineSeparator());        String actual = lines[lines.length - 1];        assertEquals(expected, actual);    }}