package Yapper;import org.junit.jupiter.api.Test;import java.time.LocalDateTime;import static org.junit.jupiter.api.Assertions.assertEquals;public class TaskListTest {    @Test    public void stringToDateTimeTest0() {        String dateTime = "2024/12/31";        LocalDateTime actual = TaskList.stringToDateTime(dateTime, 0);        LocalDateTime expected = LocalDateTime.of(2024, 12,31,00,00);        assertEquals(expected, actual);    }    @Test    public void stringToDateTimeTest1() {        String dateTime = "2024/12/31";        LocalDateTime actual = TaskList.stringToDateTime(dateTime, 1);        LocalDateTime expected = LocalDateTime.of(2024, 12,31,23,59);        assertEquals(expected, actual);    }    @Test    public void stringToDateTimeTestWithTime() {        String dateTime = "2024/12/31 1800";        LocalDateTime actual = TaskList.stringToDateTime(dateTime, 0);        LocalDateTime expected = LocalDateTime.of(2024, 12,31,18,00);        assertEquals(expected, actual);    }}