package Yapper;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.assertEquals;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.PrintStream;public class ToDoTest {    @Test    public void testToDoNoDescription() {        String userInput = String.format("todo%sbye", System.lineSeparator());        ByteArrayInputStream bais = new ByteArrayInputStream(userInput.getBytes());        System.setIn(bais);        String expected = "Description for ToDo cannot be empty!";        ByteArrayOutputStream baos = new ByteArrayOutputStream();        PrintStream printStream = new PrintStream(baos);        System.setOut(printStream);        Yapper.main(null);        String[] lines = baos.toString().split(System.lineSeparator());        String actual = lines[lines.length - 1];        assertEquals(expected, actual);    }}